import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCloud, faSun, faCloudRain, faSnowflake } from '@fortawesome/free-solid-svg-icons';

const Weather = () => {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);
    const [error, setError] = useState('');

    const fetchWeather = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/weather/${city}`);
            setWeather(response.data);
            setError('');
        } catch (err) {
            setError('City not found. Please try again.');
            setWeather(null);
        }
    };

    const getWeatherIcon = (description) => {
        if (description.includes('cloud')) return faCloud;
        if (description.includes('rain')) return faCloudRain;
        if (description.includes('snow')) return faSnowflake;
        if (description.includes('clear')) return faSun;
        return faCloud; // Default icon
    };

    return (
        <div className="App">
            <h1>Weather App</h1>
            <input
                type="text"
                value={city}
                onChange={(e) => setCity(e.target.value)}
                placeholder="Enter city name"
            />
            <button onClick={fetchWeather}>Get Weather</button>

            {error && <p className="error-message">{error}</p>}

            {weather && (
                <div className="weather-container">
                    <h2>
                        {weather.name}, {weather.sys.country}
                    </h2>
                    <FontAwesomeIcon
                        icon={getWeatherIcon(weather.weather[0].description)}
                        size="3x"
                        style={{ color: '#4dabf7', marginBottom: '10px' }}
                    />
                    <p>Temperature: {weather.main.temp}Â°C</p>
                    <p>Weather: {weather.weather[0].description}</p>
                    <p>Humidity: {weather.main.humidity}%</p>
                </div>
            )}
        </div>
    );
};

export default Weather;
